mapper.select:
SELECT CODE, GOODS_PHOTO_PATH, TITLE, DEADLINE, PERSON_PRICE, COUNT, REGION, LOCATION_X, LOCATION_Y, WRITE_DATETIME
FROM VIEW_BUYPOST_MAIN
WHERE (TO_DATE(SYSDATE, 'YYYY-MM-DD HH24:MI:SS') - TO_DATE(WRITE_DATETIME, 'YYYY-MM-DD HH24:MI:SS')) <= 7  AND MAIN_CATE_CODE = #{code}

SELECT COUNT(*) AS COUNT
FROM VIEW_BUYPOST_MAIN
WHERE (TO_DATE(SYSDATE, 'YYYY-MM-DD HH24:MI:SS') - TO_DATE(WRITE_DATETIME, 'YYYY-MM-DD HH24:MI:SS')) <= 7  AND MAIN_CATE_CODE = #{code}

SELECT CODE, GOODS_PHOTO_PATH, TITLE, DEADLINE, PERSON_PRICE, COUNT, REGION, LOCATION_X, LOCATION_Y, SUB_CATE_CODE, MAIN_CATE_CODE
FROM VIEW_BUYPOST_CATE
WHERE MAIN_CATE_CODE = #{code}

SELECT COUNT(*) AS COUNT
FROM VIEW_BUYPOST_CATE
WHERE MAIN_CATE_CODE = #{code}

SELECT CODE, GOODS_PHOTO_PATH, TITLE, DEADLINE, PERSON_PRICE, COUNT, REGION, LOCATION_X, LOCATION_Y, SUB_CATE_CODE, MAIN_CATE_CODE
FROM VIEW_BUYPOST_CATE
WHERE SUB_CATE_CODE = #{code}

SELECT COUNT(*) AS COUNT
FROM VIEW_BUYPOST_CATE
WHERE SUB_CATE_CODE = #{code}
